(158, 1, '2016-01-26 08:43:43', '2016-01-26 08:43:43', '\r\n<div class="content-section-a">\r\n\r\n        <div class="container">\r\n            <div class="row">\r\n                <div class="col-lg-5 col-sm-6">\r\n                    <hr class="section-heading-spacer">\r\n                    <div class="clearfix"></div>\r\n                    <h2 class="section-heading">Death to the Stock Photo:<br>Special Thanks</h2>\r\n                    <p class="lead">A special thanks to <a target="_blank" href="http://join.deathtothestockphoto.com/">Death to the Stock Photo</a> for providing the photographs that you see in this template. Visit their website to become a member.</p>\r\n                </div>\r\n                <div class="col-lg-5 col-lg-offset-2 col-sm-6">\r\n                    <img class="img-responsive" src="http://localhost:8888/localwp.com/wp-content/uploads/2016/01/card1-300x214.jpg" alt="">\r\n                </div>\r\n            </div>\r\n\r\n        </div>\r\n        <!-- /.container -->\r\n\r\n    </div>\r\n    <!-- /.content-section-a -->\r\n\r\n    <div class="content-section-b">\r\n\r\n        <div class="container">\r\n\r\n            <div class="row">\r\n                <div class="col-lg-5 col-lg-offset-1 col-sm-push-6  col-sm-6">\r\n                    <hr class="section-heading-spacer">\r\n                    <div class="clearfix"></div>\r\n                    <h2 class="section-heading">3D Device Mockups<br>by PSDCovers</h2>\r\n                    <p class="lead">Turn your 2D designs into high quality, 3D product shots in seconds using free Photoshop actions by <a target="_blank" href="http://www.psdcovers.com/">PSDCovers</a>! Visit their website to download some of their awesome, free photoshop actions!</p>\r\n                </div>\r\n                <div class="col-lg-5 col-sm-pull-6  col-sm-6">\r\n                    <img class="img-responsive" src="http://localhost:8888/localwp.com/wp-content/uploads/2016/01/card2-300x215.jpg" alt="">\r\n                </div>\r\n            </div>\r\n\r\n        </div>\r\n        <!-- /.container -->\r\n\r\n    </div>\r\n    <!-- /.content-section-b -->\r\n\r\n    <div class="content-section-a">\r\n\r\n        <div class="container">\r\n\r\n            <div class="row">\r\n                <div class="col-lg-5 col-sm-6">\r\n                    <hr class="section-heading-spacer">\r\n                    <div class="clearfix"></div>\r\n                    <h2 class="section-heading">Google Web Fonts and<br>Font Awesome Icons</h2>\r\n                    <p class="lead">This template features the ''Lato'' font, part of the <a target="_blank" href="http://www.google.com/fonts">Google Web Font library</a>, as well as <a target="_blank" href="http://fontawesome.io">icons from Font Awesome</a>.</p>\r\n                </div>\r\n                <div class="col-lg-5 col-lg-offset-2 col-sm-6">\r\n                    <img class="img-responsive" src="http://localhost:8888/localwp.com/wp-content/uploads/2016/01/card3-300x207.jpg" alt="">\r\n                </div>\r\n            </div>\r\n\r\n        </div>\r\n        <!-- /.container -->\r\n\r\n    </div>\r\n    <!-- /.content-section-a -->\r\n\r\n\r\n\r\n', '[:en]Project[:ja]プロジェクト[:]', '', 'inherit', 'closed', 'closed', '', '29-revision-v1', '', '', '2016-01-26 08:43:43', '2016-01-26 08:43:43', '', 29, 'http://localhost:8888/localwp.com/2016/01/26/29-revision-v1/', 0, 'revision', '', 0),
