(16, 1, '2016-01-06 06:34:03', '2016-01-06 06:34:03', '[:en] <div class="col-md-6">  \r\n     	<section id="home">\r\n        <div class="container">\r\n            <div class="row">\r\n                <div class="col-md-12 text-center" id="home_pages_dynamic"> \r\n			<h2 class="section-heading">OKEON </h2>\r\n			<hr class="home-top">\r\n		        <p>Okinawa Environmental Observation Network</p>\r\n                        <hr class="home-bottom">\r\n                        <p class="intro"> OKEON is a network to monitor the terrestrial environment of Okinawa, involving researchers at OIST, collaborators a various institutions, museums and high schools across Okinawa, and the local community</p>\r\n                        <i class="fa fa-2x fa-cubes icon-text"><a href="#"></a></i>\r\n                        <p class="intro"> The main components of OKEON include a field network, sample processing capacity, ecological genomics pipeline, and environmental informatics capacity</p>\r\n                       \r\n                </div>\r\n            </div>\r\n        </div>\r\n    </section>\r\n</div>[:ja]<div class="col-md-6">  \r\n     	<section id="home">\r\n        <div class="container">\r\n            <div class="row">\r\n                <div class="col-md-12 text-center" id="home_pages_dynamic"> \r\n			<h2 class="section-heading">美ら森プロジェクト </h2>\r\n			\r\n		        Okinawa Environmental Observation Network\r\n                        <hr class="home">\r\n                        <p class="intro"> OKEON is a network to monitor the terrestrial environment of Okinawa, involving researchers at OIST, collaborators a various institutions, museums and high schools across Okinawa, and the local community</p>\r\n                        <p class="intro"> The main components of OKEON include a field network, sample processing capacity, ecological genomics pipeline, and environmental informatics capacity</p>\r\n\r\n\r\n                </div>\r\n            </div>\r\n        </div>\r\n    </section>\r\n</div>[:]', '[:en]Home[:ja]ホーム[:]', '', 'publish', 'closed', 'closed', '', 'home-2', '', '', '2016-03-17 02:36:34', '2016-03-17 02:36:34', '', 0, 'http://localhost:8888/localwp.com/?page_id=16', 0, 'page', '', 0),
